import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';

import { dialogsActions} from '../redux/actions';
import socket from "core/socket"
import { Dialogs as BaseDialogs } from "components"


const Dialogs = ({ fetchDialogs, setCurrentDialogId, currentDialogId, items, userId }) => {
    const [inputValue, setValue] = useState("")
    let [filted, setFilteredItems] = useState(Array.from(items))

    const onChangeInput = value => {
        setFilteredItems(
            items.filter(
                dialog =>
                    dialog.user.fullname.toLowerCase().indexOf(value.toLowerCase()) >= 0
            )
        )
        setValue(value)
    }

    const onNewDialog = () => {
        fetchDialogs()
    } 

    useEffect (() => {

    }, [items])

    useEffect(() => {
        fetchDialogs()
        /* if (!items.length) {
            fetchDialogs()
        } else {
            setFilteredItems(items)
        } */

        socket.on("SERVER:DIALOG_CREATED", onNewDialog)
        return () => {
            socket.removeListener("SERVER:DIALOG_CREATED", onNewDialog)
        }
        
    }, [/* items */])


    return <BaseDialogs
        userId={userId}
        items={filted}
        inputValue={inputValue}
        currentDialogId={currentDialogId}
        onSelect={setCurrentDialogId}
        onSearch={onChangeInput}

    ></BaseDialogs>
};

export default connect(({ dialogs }) => dialogs, dialogsActions)(Dialogs)
