const initialState = {
    items: []
}

export default (state = initialState, { type, payload }) => {
    switch (type) {
        case "ATTACHMENTS:ADD_FILE":
            return {
                ...state,
                data: payload,
                token: window.localStorage.token,
                isAuth: true
            }
        case "ATTACHMENTS:REMOVE_FILE":
            return {
                ...state,
                data: payload,
                token: window.localStorage.token,
                isAuth: true
            }
        
        default:
            return state
    }
}
