export default ({isAuth}) => ({
    email: (errors, value) => {
        if (!value) {
            errors.email = "Введите E-mail"
        } else if (
            !/^[A-Z0-9._%+-]+@[A-Z0-9._%+-]+\.[A-Z]{2,4}$/i.test(value)
        ) {
            errors.email = "Не верный E-mail"
        }
    },
    password: (errors, value) => {
        if (!value) {
            errors.password = "Введите пороль"
        } else if (!/^.*(?=.{8,})(?=.*[a-zA-Z])(?=.*\d).*$/i.test(value)) {
            errors.password = isAuth ? "Не верный пароль" : "Слишком легкий пароль"
        }
    }
})
