import { withFormik } from 'formik';
import RegisterForm from "../components/RegisterForm";
import { valid } from 'semver';

export default withFormik({
    mapPropsToValues: () => (
        {
            email: "",
            password: ""
        }),
    validate: values => {
        let errors = {}

        const keys = Object.keys(values)
        keys.forEach(key => validate(errors, key, values))
        if (!values.email) {
            errors.email = "Введите вашу почту"
        } else if (
            !/^[A-Z0-9._%+-]+@[A-Z0-9._%+-]+\.[A-Z]{2,4}$/i.test(values.email)
        ) {
            errors.email = "Invalid email address"
        }
        if (!values.password) {
            errors.password = "Введите пороль"
        } else if (!/^.*(?=.{8,})(?=.*[a-zA-Z])(?=.*\d).*$/i.test(values.password)) {
            errors.password = "Слишком легкий пороль"
        }
        return errors

    },
    handleSubmit: (values, { setSubmitting }) => {
        setTimeout(() => {
            alert(JSON.stringify(values, null, 2))
            setSubmitting(false)
        }, 1000);
    },
    displayName: "RegisterForm"

})(RegisterForm);
