import React, { useState } from 'react';
import classNames from 'classnames';
import { SmileOutlined, PictureOutlined, AudioOutlined, SendOutlined } from '@ant-design/icons'
import { Input, Button } from "antd"
import { UploadField } from '@navjobs/upload'

import "./ChatInput.scss";

const ChatInput = (props) => {
    const [value, setValue] = useState('')

    return <div className="chat-input">
        <div className="chat-input__smile-btn">
            <Button type='text' icon={<SmileOutlined />}></Button>
        </div>
        <Input onChange={e => setValue(e.target.value)} size='large' placeholder="Введите текст сообщения…" />
        <div className="chat-input__actions">
            <UploadField
                onFiles={files => { console.log(files) }}
                containerProps={{
                    className: 'photos'
                }}
                uploadProps={{
                    accept: '.jpg,.jpeg,.png,.gif,.bmp',
                }}
            >
                <Button type='text' icon={<PictureOutlined />} ></Button>
            </UploadField>
            {value ? <Button type='text' icon={<SendOutlined />}></Button> : <Button type='text' icon={<AudioOutlined />}></Button>}
        </div>
    </div >
}


export default ChatInput;
