import { connect } from "react-redux";
import { withFormik } from "formik";

import LoginForm from "../components/LoginForm";

import validateForm from "utils/validate"
import { userActions } from "../../../redux/actions" /* не получается "redux/actions", ругается на пакет redux */

import store from "redux/store"

const LoginFormConnected = connect(state => state, userActions)(LoginForm)

const LoginFormContainer = withFormik({
    mapPropsToValues: () => (
        {
            email: "",
            password: ""
        }),
    validate: values => {
        let errors = {}

        validateForm({ isAuth: true, values, errors })

        return errors
    },
    handleSubmit: (values, { setSubmitting }) => {
        console.log(fetchUserLogin)
        return axios.post("/user/login", values)
            .then(({ data }) => {
                setSubmitting(false)
            })
            .catche(() => {
                setSubmitting(false)
            })
    },
    displayName: "LoginForm"

})(LoginFormConnected);

export default LoginFormContainer
