export default {
    email: (errors, value) => {
        if (!value.email) {
            errors.email = "Введите вашу почту"
        } else if (
            !/^[A-Z0-9._%+-]+@[A-Z0-9._%+-]+\.[A-Z]{2,4}$/i.test(value.email)
        ) {
            errors.email = "Invalid email address"
        }
    },
    password: (errors, value) => {
        if (!value.password) {
            errors.password = "Введите пороль"
        } else if (!/^.*(?=.{8,})(?=.*[a-zA-Z])(?=.*\d).*$/i.test(value.password)) {
            errors.password = "Слишком легкий пороль"
        }
    }
}
