import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { dialogsActions, messagesActions } from '../redux/actions';
import socket from "core/socket";
import { Dialogs as BaseDialogs } from "components";

const Dialogs = ({ fetchDialogs, currentDialogId, items, userId, messages }) => {
    const [inputValue, setInputValue] = useState("");
    const [filteredItems, setFilteredItems] = useState([]);
    const [unreadCount, setUnreadCount] = useState(0);

    const onNewDialog = () => {
        fetchDialogs();
    };

    const onChangeInput = (value) => {
        setInputValue(value);
    };

    useEffect(() => {
        fetchDialogs();

        socket.on("SERVER:DIALOG_CREATED", fetchDialogs);
        socket.on("SERVER:NEW_MESSAGE", fetchDialogs);

        return () => {
            socket.removeListener("SERVER:DIALOG_CREATED", fetchDialogs);
            socket.removeListener("SERVER:NEW_MESSAGE", fetchDialogs);
        }
    }, []);

    useEffect(() => {
        const sortedItems = [...items].sort((a, b) => new Date(b.updatedAt) - new Date(a.updatedAt));
        const filteredSortedItems = sortedItems.filter(
            dialog =>
                dialog.author.fullname.toLowerCase().includes(inputValue.toLowerCase()) ||
                dialog.partner.fullname.toLowerCase().includes(inputValue.toLowerCase())
        );
        setFilteredItems(filteredSortedItems);

        // Calculate unread messages count
        const count = items.reduce((total, dialog) => {
            return total + dialog.messages.items.reduce((unreadTotal, message) => {
                return unreadTotal + (message.read ? 0 : 1);
            }, 0);
        }, 0);
        setUnreadCount(count);
    }, [inputValue, items]);

    return (
        <BaseDialogs
            userId={userId}
            items={filteredItems}
            inputValue={inputValue}
            currentDialogId={currentDialogId}
            onSearch={onChangeInput}
            unreadCount={unreadCount}
        />
    );
};

export default connect(
    ({ dialogs, messages }) => ({ dialogs, messages: messages }),
    dialogsActions
)(Dialogs);
