import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';

import { dialogsActions } from '../redux/actions';
import socket from "core/socket"
import { Dialogs as BaseDialogs } from "components"


const Dialogs = ({ fetchDialogs, currentDialogId, items, userId }) => {
    const [inputValue, setValue] = useState("")
    let [filted, setFilteredItems] = useState(Array.from(items))

    const onChangeInput = (value = "") => {
        setFilteredItems(
            items.filter(
                dialog =>
                    dialog.author.fullname.toLowerCase().indexOf(value.toLowerCase()) >= 0 ||
                    dialog.partner.fullname.toLowerCase().indexOf(value.toLowerCase()) >= 0
            )
        )
        setValue(value)
    }

    const onNewDialog = () => {
        fetchDialogs()
    }

    useEffect(() => {
        if (items.length) {
            const sortedItems = [...items].sort((a, b) => {
                return new Date(b.updatedAt) - new Date(a.updatedAt);
            });
            setFilteredItems(sortedItems);
        }
    }, [items])

    useEffect(() => {
        fetchDialogs()

        socket.on("SERVER:DIALOG_CREATED", fetchDialogs)
        socket.on("SERVER:NEW_MESSAGE", fetchDialogs)
        return () => {
            socket.removeListener("SERVER:DIALOG_CREATED", fetchDialogs)
            socket.removeListener("SERVER:NEW_MESSAGE", fetchDialogs)
        }

    }, [])


    return <BaseDialogs
        userId={userId}
        items={filted}
        inputValue={inputValue}
        currentDialogId={currentDialogId}
        onSearch={onChangeInput}

    ></BaseDialogs>
};

export default connect(({ dialogs }) => dialogs, dialogsActions)(Dialogs)
