import React from 'react';
import classNames from 'classnames';
import { Time, IconReaded, Avatar } from "components"
import isToday from "date-fns/isToday"
import format from "date-fns/format"
import { Link } from "react-router-dom"

const getMessageTime = (created_at) => {
    if (isToday(created_at)) {
        return format(created_at, 'HH:mm')
    } else {
        return format(created_at, 'dd.MM.yy')
    }
}

const DialogItem = ({
    _id,
    user,
    partner,
    lastMessage,
    currentDialogId,
    read,
    isMe,
    userId,
}) => {
    console.log(_id, isMe, userId)
    return <Link to={`/dialog/${_id}`}>
        <div
            className={classNames('dialogs__item', {
                "dialogs__item--online": partner.isOnline,
                "dialogs__item--selected": currentDialogId === _id
            })}
        >
            <div className="dialogs__item-avatar"><Avatar user={partner}></Avatar></div>
            <div className="dialogs__item-info">
                <div className="dialogs__item-info-top">
                    <b>{partner.fullname}</b>
                    <span>
                        {/* {<Time date={lastMessage.created_at}></Time>} */}
                        {getMessageTime(lastMessage.createdAt)}
                    </span>
                </div>
                <div className="dialogs__item-info-bottom">
                    <p>
                        {lastMessage.user._id === userId ? `Вы: ${lastMessage.text}` : lastMessage.text}
                    </p>
                    {isMe && <IconReaded isMe={isMe} isReaded={read}></IconReaded>}
                    {lastMessage.unreaded > 0 && <div className="dialogs__item-info-bottom-count">{lastMessage.unreaded > 99 ? "+99" : lastMessage.unreaded}</div>}
                </div>
            </div>
        </div>
    </Link>
}


export default DialogItem;
