import React, { useEffect, useState } from 'react';
import { Result } from 'antd';

import { userApi } from "utils/api"
import { Block } from 'components';

const renderTextInfo = (hash, verified) => {
    if (hash) {
        if (verified) {
            return "Аккаунт успешно подтвержден!"
        } else {
            return "Ошибка при подтверждении аккаунта"
        }
    } else {
        return "Ссылка с подтверждением аккаунта отправлена на E-Mail."
    }
}

const CheckEmailInfo = ({ location }) => {
    const [verified, setVerified] = useState(false)

    useEffect(() => {
        const hash = location.search.split('hash=')[1]
        if (hash) {
            userApi.verifyHash(hash)
                .then(({ data }) => {
                    if (data.status === "success") {
                        setVerified(true)
                    }
                })
        }
    })

    return (
        <div>
            <Block>
                <Result
                    status="success"
                    title="Готово!"
                    subTitle={
                        !verified ? (
                            <p>
                                Регистрация прошла успешно!
                                <br />
                                Ссылка с подтверждением аккаунта отправлена на E-Mail.
                            </p>
                        ) : (
                            <p> Аккаунт успешно подтвержден!</p>
                        )
                    }
                    style={{ userSelect: 'none' }}
                />
            </Block>
        </div>
    );
};

export default CheckEmailInfo;
