import React, { useEffect, useState } from 'react';
import propTypes from "prop-types"
import classNames from 'classnames';
import { Message } from "components"
import { Empty, Spin } from "antd"

import "./Messages.scss"

const Messages = ({ onRemoveMessage, blockRef, items, user, isLoading }) => {
    /* const [chatInputHeight, setHeight] = useState(130)

    useEffect(() => {
        let chatInputHeight = document.querySelector(".chat__dialog-input").clientHeight
    }, []) */

    return (
        <div
            className="chat__dialog-messages"
            style={{ height: `calc(100% - ${chatInputHeight}px)` }
            }>
            <div ref={blockRef} className={classNames("messages", { "messages--loading": isLoading })}>
                {isLoading ? (
                    <Spin size='large'></Spin>
                ) : items && !isLoading ? (
                    items.length > 0 ? (
                        items.map(item => (
                            <Message
                                key={item._id}
                                {...item}
                                isMe={user._id === item.user._id}
                                onRemoveMessage={onRemoveMessage.bind(this, item._id)}
                            ></Message>
                        ))
                    ) : (
                        <Empty description="Диалог пустой" />
                    )
                ) : (
                    <Empty description="Откройте диалог" />
                )
                }
            </div>
        </div>
    )
}

Messages.propTypes = {
    items: propTypes.array
}

export default Messages;
