import React, { useEffect, useState } from 'react';
import classNames from 'classnames';
import { SmileOutlined, PictureOutlined, AudioOutlined, SendOutlined } from '@ant-design/icons'
import { Input, Button } from "antd"
import { UploadField } from '@navjobs/upload'
import { Picker } from 'emoji-mart'

import "./ChatInput.scss";

const { TextArea } = Input

const ChatInput = (props) => {
    const [value, setValue] = useState('')
    const [emojiPickerVisible, setShowEmojiPicker] = useState(false)

    const { onSendMessage, currentDialogId } = props

    const toogleEmojiPicker = () => {
        setShowEmojiPicker(!emojiPickerVisible)
    }

    const handleSendMessage = (e) => {
        if (e.key === "Enter" && e.shiftKey) {
            setValue(value => value + "\n")
        }
        else if (e.key === "Enter" && value.trim() !== "") {
            onSendMessage(value, currentDialogId)
            setValue("")
        }
    }

    const handleOnKeyDown = (e) => {
        if (e.key === "Enter") {
            e.preventDefault()
        }
    }

    const addEmoji = ({ colons }) => {
        setValue((value + " " + colons).trim())
    }

    const handleOutsideClick = (el, e) => {
        if (el && !el.contains(e.target)) {
            setShowEmojiPicker(false)
        }
    }

    useEffect(() => {
        const el = document.querySelector(".chat-input__smile-btn")

        document.addEventListener("click", handleOutsideClick.bind(this, el))

        return () => {
            document.removeEventListener("click", handleOutsideClick.bind(this, el))
        }
    })

    return <div className="chat__dialog-input">
        <div className="chat-input">
            <div className="chat-input__smile-btn">
                <div className="chat-input__emoji-picker">
                    {emojiPickerVisible && (
                        <Picker
                            set='google'
                            emojiSize={26}
                            style={{
                                width: "375px"
                            }}
                            onSelect={(emojiTag) => addEmoji(emojiTag)}
                        ></Picker>
                    )}
                </div>
                <Button type='text' icon={<SmileOutlined />} onClick={toogleEmojiPicker}></Button>
            </div>
            <TextArea
                size='large'
                placeholder="Введите текст сообщения…"
                value={value}
                autoSize={{ minRows: 1, maxRows: 6 }}
                onChange={e => setValue(e.target.value)}
                onKeyUp={handleSendMessage}
                onKeyDown={handleOnKeyDown}

            />
            <div className="chat-input__actions">
                <UploadField
                    onFiles={files => { console.log(files) }}
                    containerProps={{
                        className: 'chat-input__actions-upload-btn'
                    }}
                    uploadProps={{
                        accept: '.jpg,.jpeg,.png,.gif,.bmp',
                        multiple: "multiple"
                    }}
                >
                    <Button type='text' icon={<PictureOutlined />} ></Button>
                </UploadField>
                {value ? <Button type='text' icon={<SendOutlined />}></Button> : <Button type='text' icon={<AudioOutlined />}></Button>}
            </div>
        </div >
    </div>
}


export default ChatInput;
