import { TeamOutlined, FormOutlined } from '@ant-design/icons'
import { Button, Modal, Select } from "antd"
import { Dialogs } from 'containers';

const Sidebar = ({ user }) => {
    const [visible, setVisible] = useState(false)

    const onClose = () => {
        setVisible(false)
    }

    return <div className="chat__sitebar">
        <div className="chat__sitebar-header">
            <div>
                <TeamOutlined />
                <span>Список диалогов</span>
            </div>
            <Button 
            type='text' 
            size='small' 
            icon={<FormOutlined />}
            onClick={() => {setVisible(true)}}
            />
        </div>
        <div className="chat__sitebar-dialogs">
            <Dialogs userId={user && user._id}></Dialogs>
        </div>
        <Modal title="Создать диалог" open={visible} onOk={onClose} onCancel={onClose}>

        </Modal>
    </div>
};

export default Sidebar;
