const initialState = {
    data: null,
    token: "",
    isAuth: false
}

export default (state = initialState, { type, payload }) => {
    switch (type) {
        case "USER:SET_DATA":
            return {
                ...state,
                data: payload,
                token: window.localStorage.token,
                isAuth: true
            }
        default:
            return state
    }
}
