import React from 'react';
import { TeamOutlined, FormOutlined } from '@ant-design/icons'
import { Button, Modal, Select } from "antd"
import { Dialogs } from 'containers';

const { Option } = Select

const Sidebar = ({ user, visible, onShow, onClose }) => {
    return <div className="chat__sitebar">
        <div className="chat__sitebar-header">
            <div>
                <TeamOutlined />
                <span>Список диалогов</span>
            </div>
            <Button
                type='text'
                size='small'
                icon={<FormOutlined />}
                onClick={onShow}
            />
        </div>
        <div className="chat__sitebar-dialogs">
            <Dialogs userId={user && user._id}></Dialogs>
        </div>
        <Modal title="Создать диалог" open={visible} onOk={onClose} onCancel={onClose}>
            <Select
                showSearch
                value={value}
                placeholder={props.placeholder}
                style={props.style}
                defaultActiveFirstOption={false}
                suffixIcon={null}
                filterOption={false}
                onSearch={handleSearch}
                onChange={handleChange}
                notFoundContent={null}
                options={(data || []).map((d) => ({
                    value: d.value,
                    label: d.text,
                }))}
            />
        </Modal>
    </div>
};

export default Sidebar;
