import React from 'react';
import propTypes from "prop-types"
import classNames from 'classnames';
import { Time, IconReaded } from "components"
import isToday from "date-fns/isToday"
import format from "date-fns/format"


import "./DialogItem.scss"

const getMessageTime = (created_at) => {
    if (isToday(created_at)) {
        return format(created_at, 'HH:mm')
    } else {
        return format(created_at, 'dd.MM.yy')
    }
}

const getAvatar = (avatar) => {
    if (avatar) {
        return (
            <img
                src={avatar}
                alt=""
            />
        )
    } else {
        /* avatar default */
    }
}

const DialogItem = ({ user, lastMessage, unreaded, isMe, isReaded }) => {
    return <div className={classNames('dialogs__item', {"dialogs__item--online": user.isOnline})}>
        <div className="dialogs__item-avatar">
            <img src={user.avatar} alt={`${user.fullname} avatar`} />
        </div>
        <div className="dialogs__item-info">
            <div className="dialogs__item-info-top">
                <b>{user.fullname}</b>
                <span>
                    {/* {<Time date={lastMessage.created_at}></Time>} */}
                    {getMessageTime(lastMessage.created_at)}
                </span>
            </div>
            <div className="dialogs__item-info-bottom">
                <p>
                    {lastMessage.text}
                </p>
                {isMe && <IconReaded isMe={true} isReaded={isReaded}></IconReaded>}
                {unreaded > 0 && <div className="dialogs__item-info-bottom-count">{unreaded > 99 ? "+99": unreaded}</div>}
            </div>
        </div>
    </div>
}


export default DialogItem;
