import React, { useState } from 'react';
import { connect } from "react-redux"
import { Sidebar as SidebarBase } from 'components';

const Sidebar = ({ user }) => {
    const [visible, setVisible] = useState(false)

    const onClose = () => {
        setVisible(false)
    }
    return (
        <SidebarBase
            user={user}
            visible={visible}
            onShow={onShow}
            onClose={onClose}
        />
    )
}

export default connect(({ user }) => ({ user: user.data }))(Sidebar);
