import { combineReducers } from "redux";

const reducers = ["messages", "dialogs", "user", "attachments"]

export default a =  combineReducers(
    reducers.reduce((initial, name) => {
        initial[name] = require(`./${name}`).default
        return initial
    }, {})
)

console.log(a)
