import React from 'react';
import propTypes from "prop-types"
import classNames from 'classnames';
import formatDistanceToNow from "date-fns/formatDistanceToNow"
import ruLovale from "date-fns/locale/ru";
import checkedSvg from "assets/img/checked.svg"

import "./Message.scss"

const Message = ({ avatar, user, text, date, isMe }) => {
    return <div className={classNames('message', { 'message--isme': isMe })}>
        <div className='message__content'>
            <img src={checkedSvg} alt='checked icon' />
            <div>
                <div className='message__avatar'>
                    <img src={avatar} alt={`User ${user/* .fullname */}`} />
                </div>
                <div className='message__info'>
                    <div className='message__bubble'>
                        <p className='message__text'>{text}</p>
                    </div>
                    <span className='message__date'>{formatDistanceToNow(date, { addSuffix: true, locale: ruLovale })}</span>
                </div>
            </div>
        </div>
    </div>
}

Message.defaultProps = {
    user: {}
}

Message.propTypes = {
    avatar: propTypes.string,
    user: propTypes.string,
    text: propTypes.string,
    date: propTypes.object
}

export default Message;
